import React from "react";
import { SafeAreaView, StyleSheet, Text, Image, Button, TouchableOpacity, View, TextInput } from "react-native";

import { useNavigation } from "@react-navigation/native";






export default function Login(props) {
    const navigation = useNavigation()

    return (
        <SafeAreaView style={styles.container}>
          
            <Image style={styles.Image} source={require("../../../assets/andro1.jpg")} />
            <View>
                <TextInput placeholder="Email" style={styles.input} />
                <TextInput placeholder="Senha" style={styles.input} />
            </View>
            <TouchableOpacity onPress={() => navigation.navigate("Dashiboard")}>
                    <Text style={styles.tela1}>Entrar</Text>
                </TouchableOpacity>

            <View style={{ flexDirection: "row", justifyContent: 'space-between' }}>
                <TouchableOpacity onPress={() => navigation.navigate("Cadusuario")}>
                    <Text style={styles.tela2}>Cadastra usuario</Text>
                </TouchableOpacity>
               
                </View>
        </SafeAreaView>

    );
}
const styles = StyleSheet.create({
    container: {
        flex: 1,
        backgroundColor: '#fff',
    },
    tela1: {
        fontSize:20,
        marginTop: 30,
        marginBottom: 5,
       padding:20,
        borderWidth: 1,
        backgroundColor: "#9DF545",
        textAlign:"center"
    },
    tela2:{
        fontSize:20,
        marginTop: 30,
        marginBottom: 5,
       padding:20,
        borderWidth: 1,
        backgroundColor: "#42EBCD",
        textAlign:"center"

    },

    Image: {
        fontSize: 40,
        marginTop: 30,
        height: 30,
        padding: 20,
        resizeMode: 'contain',
    },
})
