import React from "react";
import { SafeAreaView,StyleSheet,Text,View,TouchableOpacity,Image, TextInput } from "react-native";
import { useNavigation} from "@react-navigation/native";



export default function Login(){
    const navigation=useNavigation()

    return(
        <SafeAreaView style={styles.container}>
        <Image  style={styles.Image} source={require("../../../assets/fotocapa.jpg")}/>

        <TextInput style={styles.input} placeholder="Email"/>
        <TextInput style={styles.input1}  placeholder="senha"/>
       
           
            <TouchableOpacity onPress={()=> navigation.navigate("Cadastro")}>
                <Text style={styles}> MUDAR DE TELA </Text>
            </TouchableOpacity>
        </SafeAreaView>
    );
}
const styles = StyleSheet.create({
    container: {
        flex: 1,
        backgroundColor: '#fff',
    },
     Image:{
        fontSize:30,
        marginStart:30,
     },
     input:{
        fontSize:20,
        borderWidth: 1,
        marginTop:90,
        marginBottom:5,
        textAlign:"center",
     },
     input1:{
        fontSize:20,
        borderWidth: 1,
        marginBottom:5,
        textAlign:"center",
     },
   tela: {
        fontSize: 20,
        marginTop:10,
        marginBottom:5,
        borderWidth: 1,
        borderRadius:60,
        width:"50%",
        padding:10,
        backgroundColor:"#E0460D",
        
    },
    tela1:{
        fontSize: 20,
        marginTop:10,
        marginBottom:5,
        padding:10,
    },
});
