import React from "react";
import { SafeAreaView, StyleSheet, Text, TouchableOpacity, View, Image } from "react-native";

import { useNavigation } from "@react-navigation/native";




export default function Noticia() {
    const navigation = useNavigation()

    return (
        <SafeAreaView style={styles.container} >
            <Text style={styles.input}>Noticia</Text>
            <View></View>
            <Image style={styles.Image} source={require("../../../assets/fotocapa.jpg")} />

            <TouchableOpacity onPress={() => navigation.navigate("Testo")}>
                <Text style={styles.input1}>Ir para outra tela </Text>
            </TouchableOpacity>
        </SafeAreaView>

    );
}
const styles = StyleSheet.create({
    container: {
        flex: 1,
        backgroundColor: '#fff',
    },
    Image: {

        flex: 1,
        width: 100,
        height: 90,
        marginTop: 100,
        resizeMode: 'contain'
    },
    input: {
        fontSize: 25,
        marginTop: 2,
        padding: 7,
        marginBottom: 30,
        backgroundColor: "#850580"
    },
    input1: {
        fontSize: 25,
        borderWidth: 1,
        padding: 7.5,
        marginBottom: 5,
        textAlign: "center",
        backgroundColor: "#AFFF8B",
    },
})
