import React from "react";
import { SafeAreaView, StyleSheet,Text,TouchableOpacity,View,Image } from "react-native";

import { useNavigation}  from "@react-navigation/native";




export default function Noticia(){
    const navigation=useNavigation()
   
   return(
<SafeAreaView style={styles.container} >
    <Text style={styles.input}>Noticia</Text>
    <View style={{flexDirection:"row"}}>
    <Image style={styles.Image} source={require("../../../assets/fotocapa.jpg")} />
    <Text style={styles.input}>Android Pay? Google pode lan√ßar sistema mobile de pagamentos em 201</Text>
    </View>
    <TouchableOpacity onPress={() => navigation.navigate("Testo")}>
        <Text style={styles.input1}>Ir para outra tela </Text>
    </TouchableOpacity>
</SafeAreaView>

    );
}
const styles = StyleSheet.create({
    container: {
        flex: 1,
        backgroundColor:'#fff',
    },
    Image:{
    width: "30%",
    marginTop:40,
    height: "20%",
    marginStart: 5,
    resizeMode: 'contain',
    textAlign: "center",
},
     input: {
        fontSize: 25,
        marginTop:2,
        padding: 7,
        marginBottom: 30,
        backgroundColor: "#850580"
    },
    input1: {
        fontSize: 25,
        borderWidth: 1,
        padding: 7.5,
        marginBottom: 5,
        textAlign: "center",
        backgroundColor: "#AFFF8B",
    },
    input2: {
        fontSize: 20,
        borderWidth: 1,
        padding: 7.5,
        marginBottom: 5,
    },

})
