import React from "react";
import { SafeAreaView, StyleSheet, Text, View, TouchableOpacity, Image, TextInput } from "react-native";
import { useNavigation } from "@react-navigation/native";



export default function Login() {
    const navigation = useNavigation()

    return (
        <SafeAreaView style={styles.container}>
            <Image style={styles.Image} source={require("../../../assets/fotocapa.jpg")} />

            <TextInput style={styles.input} placeholder="E-mail" />
            <TextInput style={styles.input1} placeholder="senha" />

            <TouchableOpacity onPress={() => navigation.navigate("Noticia")}>
                <Text style={styles.tela}>Entrar</Text>
            </TouchableOpacity>

            <TouchableOpacity onPress={() => navigation.navigate("Cadastro")}>
                <Text style={styles.tela1}>Cadastrar</Text>
            </TouchableOpacity>


            <TouchableOpacity onPress={() => navigation.navigate("Cadastro")}>
                <Text style={styles.tela1}>Esquece minha senha</Text>
            </TouchableOpacity>

        </SafeAreaView>
    );
}
const styles = StyleSheet.create({
    container: {
        flex: 1,
        backgroundColor: "#A96DFC",
    },
    Image: {
        marginTop :30,
        marginStart: 30,
        height: 120,
        width: 110,
        resizeMode: "stretch",
    },
    input: {
        fontSize: 25,
        borderWidth: 1,
        marginTop: 90,
        padding: 7.5,
        marginBottom: 10,
        textAlign: "center",
        backgroundColor: "#AFFF8B"
    },
    input1: {
        fontSize: 25,
        borderWidth: 1,
        padding: 7.5,
        marginBottom: 5,
        textAlign: "center",
        backgroundColor: "#AFFF8B"
    },
    tela: {
        fontSize: 25,
        marginTop: 50,
        marginBottom: 5,
        borderWidth: 1,
        borderRadius: 60,
        textAlign: "center",
        padding: 10,
        backgroundColor: "#5A6169",
    },
    tela1: {
        fontSize: 25,
        marginTop: 10,
        marginBottom: 5,
        borderWidth: 1,
        borderRadius: 60,
        textAlign: "center",
        padding: 10,
        backgroundColor: "#5A6169",
    },

});
