import React from 'react';
import { StatusBar } from 'expo-status-bar';
import { StyleSheet, Text, TextInput, View, Button } from 'react-native';
import Notas from './Notas';




export default function Aluno(props) {
  const calcluar = () => {
    let obj = {
      nome, n1, n2, n3, n4,
      media: (Number(n1) + Number(n2) + Number(n4)) / 4
    };
    props.onAdicionar(obj);

    limpar();

  }

  const [aluno, setAluno] = React.useState();
  const [n1, setN1] = React.useState();
  const [n2, setN2] = React.useState();
  const [n3, setN3] = React.useState();
  const [n4, setN4] = React.useState();

  return (
    <View>
      <View style={styles.container}>
        <TextInput placeholder='Aluno' style={styles.input} value={aluno} onChangeText={setAluno} />
      </View>
      <View style={{ flexDirection: "row" }}>
        <TextInput style={styles.input1} placeholder='N1' value={n1} onChangeText={setN1} />
        <TextInput style={styles.input1} placeholder='N2' value={n2} onChangeText={setN2} />
        <TextInput style={styles.input1} placeholder='N3' value={n3} onChangeText={setN3} />
        <TextInput style={styles.input1} placeholder='N4' value={n4} onChangeText={setN4} />
      </View>
      <Button title='Calcular' onPress={() => props.onMostrar(Number(calcular))} />
      <View>
      </View>
    </View>

  );
}
const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff'
  },
  input: {
    padding: 10,
    fontSize: 20,
    borderWidth: 1,
    marginBottom: 10
  },
  input1: {
    width: "25%",
    fontSize: 30,
    borderWidth: 1,
    marginBottom: 10
  },
}

)
