import React from 'react';
import { StatusBar } from 'expo-status-bar';
import { StyleSheet, Text, TextInput, View } from 'react-native';


  

export default function Aluno(props) {
    const [aluno, setAlunos] = React.useState();

  return (
    <View style={styles.container}>
      <TextInput placeholder='Aluno' style={styles.input} value={aluno} onChangeText={setAlunos} />
    
    </View>

  );
}
    const styles = StyleSheet.create({
        container: {
          flex: 1,
          backgroundColor: '#fff'
        },
        input: {
            padding: 10,
            fontSize: 20,
            borderWidth: 1,
            marginBottom:10
          },
    }
    )
