import { useNavigation } from "@react-navigation/native";
import React from "react";
import {SafeAreaView,StyleSheet,Text,TouchableOpacity,View,TextInput,Image} from "react-native";





export default function Produto(){
    const navigation=useNavigation()
    return(

<SafeAreaView style={styles.container}>
<View  style={styles.textImage}>
<Image style={styles.Image} source={require("../../../assets/BARBA.jpg")} />
</View> 
<View>
    <Text style={styles.text}>Temos diversos Tipos de Cortes</Text>
    <Text style={styles.text}>1-Corte zero</Text>
    <Text style={styles.text}>2-Corte Degrade</Text>
    <Text style={styles.text}>3-Corte Undercut</Text>
    <Text style={styles.text}>4-Corte Militar</Text>
    <Text style={styles.text}>5-Corte Pompadour</Text>
    <Text style={styles.text}>6-Corte Moicano</Text>
    <Text style={styles.text}>6-Corte Surfista</Text>
    <Text style={styles.text}>6-Corte Asa Delta</Text>
    <Text style={styles.text}>6-Corte Razor Part</Text>
</View>


</SafeAreaView>

    )
}
   const styles=StyleSheet.create({
    container:{
        flex:1,
        backgroundColor:"#968C87",

    },
    Image:{ 
        width:"100%",
        height:300,
        resizeMode:"strech", 
       },
       text:{
        fontSize:2,
        marginTop:10,
        marginBottom:10,
        
        color:'#D92938',
        fontWeight: 'bold',
        textAlign:"center",  
    },
