import React from "react";
import { View, Text, Image, SafeAreaView,StyleSheet,StatusBar,TouchableOpacity} from "react-native";
import {useNavigation} from "@react-navigation/native"
import { TextInput } from "react-native-gesture-handler";
 


export default function Login() {
    const navigation=useNavigation()
    return (
    <SafeAreaView style={styles.container}>

    <View style={styles.Image}>
    <Image style={styles.Image} source={require("../../../assets/TPC.jpg")} />
    </View>

      <View style={styles.input}>
    <TextInput style={styles.input} placeholder="Usuario"/>
    <TextInput style={styles.input} placeholder="Senha"/>
    </View>

    <View styles={styles.botao}>
    <TouchableOpacity onPress={( ) => navigation.navigate("Dashboard")}>
     <Text style={styles.text}>Entrar</Text>
    </TouchableOpacity>

    <TouchableOpacity onPress={( ) => navigation=navigate("Cadastrar")}>
     <Text style={styles.text}>Mudar de tela </Text>
    </TouchableOpacity>
    </View>

    </SafeAreaView>
    )
}
const styles=StyleSheet.create({
    container:{
        flex:1,
        backgroundColor:"#968C87",
    },
    Image:{
    marginTop:50,
    width: 250,
    height: 250,
    borderRadius: 250 / 2
    },
    input:{
        marginTop:30,
        fontSize:20,
        padding:10,
        borderWidth: 1,
        textAlign:"center",
        backgroundColor:"#EDCB58",
        color: '#080702',
        fontWeight: 'bold',
    },
    input1:{
     
            marginTop:30,
            fontSize:20,
        
            padding:20,
            borderWidth: 1,
            borderRadius:40,
            textAlign:"center",
            backgroundColor:"#62BA7F",
            color: '#080702',
            fontWeight: 'bold',

    }
})
