import React from "react";
import { View, Text, Image, SafeAreaView,StyleSheet,StatusBar,TouchableOpacity} from "react-native";
import {useNavigation} from "@react-navigation/native"
import { TextInput } from "react-native-gesture-handler";
 


export default function Login() {
    const navigation=useNavigation()
    return (
        <SafeAreaView style={styles.container}>  
    <Image style={styles.Image} source={require("../../../assets/TCC.jpg")} />
    <TextInput style={styles.input} placeholder="Usuario"/>
    <TextInput style={styles.imput} placeholder="Senha"/>
     
    <TouchableOpacity onPress={( ) => navigator=navigate("Cadastrar")}>
     <Text>Mudar de tela </Text>
    </TouchableOpacity>

        </SafeAreaView>
    )
}
const styles=StyleSheet.create({
    container:{
        flex:1,
        backgroundColor:"",
    },
    Image:{
    marginTop:50,
    width: 250,
    height: 250,
    borderRadius: 250 / 2
    }
})
