import React from 'react';
import { StatusBar } from 'expo-status-bar';
import { Button, SafeAreaView, StyleSheet,TouchableHighlight,style,Text,Image, TextInput, View } from 'react-native';


export default function App() {

  const { Valor,  setValor}= React.useState ()

  const {Calcular,  setCalcular}= React.useState ()


  return (

    <SafeAreaView style={styles.container}>
    <Text styles={styles.title}>Tabuada</Text> 

    <View style={{ flexDirection: "row"}}>

    <TouchableHighlight onPress={() => Calcular()}>
          <View style={styles.botao2} >
            <Text>Calcular</Text>
          </View>
        </TouchableHighlight>    
    <TextInput style={styles.input} placeholder="Valor" />
    </View>
    <View style={styles.bloco}>
        {Calcular.map((v) => <View>{Valor + " X " + v + " = " + (Valor * v)}</View>)}
      </View>
      <View>
        {multiplicador.map((v) => (
          <View style={styles.linha}>
            <Text style={styles.linhaText}>{Valor}</Text>
            <Text style={styles.linhaText}>X</Text>
            <Text style={styles.linhaText}>{v}</Text>
            <Text style={styles.linhaText}>=</Text>
            <Text style={styles.linhaText}>{Valor * v}</Text>
          </View>
        ))}
      </View>
    </SafeAreaView>

  );
}

const styles = StyleSheet.create({
