import React from  "react";
import { StatusBar } from 'expo-status-bar';
import { StyleSheet,SafeAreaView, Text,backgroundColor,Image,View} from 'react-native';

export default function App() {
  return (
    <SafeAreaView style={styles.container}> 
      <View style={{ flexDirection: "row" }}>

    <Image style={ styles.Image}  source={require("./assets/andro.jpg")} />

    <View style={{backgroundColor:"#B519FE"}}>
      <Text style={styles.Image}>Emilio jose gombo Malungo</Text>
    </View>
    </View>
    </SafeAreaView>

  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',

  },
  Image:{
    fontSize:10,
    marginTop:30,
    width:100,
    height:70,
  },

});
