import React from  "react";

import { StyleSheet,SafeAreaView, Text,Image,View,StatusBar} from 'react-native';

export default function App() {
  return (
    <SafeAreaView style={styles.container}> 

    <StatusBar backgroundColor={"#FF9057"} barStyle={"light-content"} translucent={false}/>
    <View style={style.containerHeader}>
      <View style={styles.ImageHeader}>
    <Image style={styles.Image} source={require("./assets/android.jpg")}/>
    </View>
    <View style={styles.TextTitulo}>
      <Text style={styles.TextTitulo}> João Barba Ruiva </Text>
      <Text style={ styles.TextInter}> pirata sem navio custa acreditar, nesses pirata de hoje em dia</Text>
      <Text style={style.TextInfo}> com base nos estudos muitos piratas estão a procura de ouro negro</Text>
    </View> 

    </View>


    </SafeAreaView>

  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
   margin:2,
   padding:2,

  },
  containerHeader:{
    flexDirection:"row",
    borderWidth:1,
    borderColor:"#000000"
  },
  ImageHeader:{
    height:120,
    width:110,
    resizeMode:" stretch",
  },
  TextTitulo:{
    fontSize:25,
    fontWeight:"bold",
    color:"#0000FF"
  },
  TextInter:{
    fontSize:18,
    fontWeight:"bold"
  },
  TextInfo:{
    fontWeight:"bold"
  }


});
