import React from 'react';
import { SafeAreaView, StyleSheet, Text,TextInput, View,StatusBar, TouchableHighlight } from 'react-native';

export default function App() {
  return (
    <SafeAreaView style={styles.container}>
 <StatusBar backgroundColor='#FFFFFF' barStyle="dark-content" translucent={false} />
 <View>
  <Text style={styles.header}>Caixa de Texto</Text>

  <TextInput style={styles.input}  placeholder='Digite o Nome'
   placeholderTextColor={"#000000"}
   secureTextEntry={"false"}/>
 </View>
 <TouchableHighlight style={styles.button} >
  <Text style={styles.textInput}>Eviar</Text>
 </TouchableHighlight>
    </SafeAreaView>



  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    marginTop:5,
    alignItems:"center",
    backgroundColor: '#fff',

  }, 
  header:{
    fontSize:25,
    fontWeight:"bold"
  },
  input:{
    fontSize:18,
    marginTop:15,
    backgroundColor:"#30B6FF",
    height:40,
    borderRadius:8,
    textAlign:"center",
    fontWeight:"bold",
    color:"#FFFFFF"
  },
  button:{
    borderEndColor:"#FF0034",
    marginTop:15,
    height:40,
    borderRadius:8,
    alignItems:"center",
    justifyContent:"center"
    },
    textInput:{
