import React from 'react';
import { SafeAreaView, StyleSheet, Text, TextInput, View, StatusBar, TouchableOpacity, Button } from 'react-native';

export default function App() {
  return (
    <SafeAreaView style={styles.container}>
      <StatusBar backgroundColor='#FF7E01' barStyle="light-content" translucent={false} />

      <View style={styles.container1}>
        <Text style={styles.header}>Campos do formulario</Text>
        <TextInput style={styles.input} placeholder='Digite o 1- Nome'
          placeholderTextColor={"#FFFFFF"}
          secureTextEntry={"false"} />
      </View>

      <View style={styles.container1}>
        <TextInput style={styles.input} placeholder='Digite o 2-Nome'
          placeholderTextColor={"#FFFFFF"}
          secureTextEntry={"false"} />
      </View>


      <TouchableOpacity style={styles.botao}>
        <Text style={styles.botao1}>Enviar</Text>  
      </TouchableOpacity>


    </SafeAreaView>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    marginTop: 20,
    alignItems: "center",
    backgroundColor: '#fff',
  },
  header: {
    marginTop: 10,
    fontSize: 30,
    textAlign: "center",
    fontWeight: "bold"
  },
  input: {
    fontSize: 20,
    marginTop: 30,
    backgroundColor: "#30B6FF",
    height: 40,
    borderRadius: 8,
    textAlign: "center",
    fontWeight: "bold",
    color: "#FFFFFF"
  },
  botao: {
    fontSize: 40,
    backgroundColor: "#FF1D2B",
    marginTop: 30,
    //marginBottom:500,
    height: 40,
    borderRadius: 8,
    alignItems: "center",
    justifyContent: "center"
  },
