import { StyleSheet,StatusBar,Text, View,Button, ShadowPropTypesIOS, TextInput } from 'react-native';

    
    export default function Lista(props) {
        return (
            <View style={{flexDirection:"row"}}>
           <TextInput placeholder={props.input} style={styles.input}  />
        
         {props.items.map((item) => <Lista item={item} />)}
            </View>
        )
    }
  
    const styles = StyleSheet.create({
        container: {
            padding: 20,
            flexDirection: "row",
            flexWrap: "wrap"
        },
        text: {
            fontSize: 16,
            padding: 20,
            borderWidth: 1,
            margin: 10,
            flex: 1
        }
    });
