import React from 'react';
import { StyleSheet, SafeAreaView, Text, Image, View, TouchableHighlight, StatusBar, TextInput } from 'react-native';

export default function App() {
  const [atividade, setAtividade] = React.useState()
  const [lista, setLista] = React.useState(["TOMAR BANHO", "CAFE"])

  return (

    <SafeAreaView style={styles.container}>
      <StatusBar backgroundColor="#FF9057" barStyle="light-content" translucent={false} />

      <View style={styles.title}>
        <Text style={styles.title1}>Tarefas Diasrias</Text>
      </View>
      <View style={styles.Indroduzir}>
        <TextInput placeholder="Atividade"
          style={styles.input} value={atividade} onChangeText={setAtividade} />

        <TouchableHighlight>
          <View style={styles.Click} >
            <Text>Click</Text>
          </View>

        </TouchableHighlight>
      </View>
{lista}



    </SafeAreaView>

  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
  },
  title1: {
    fontSize: 30,
    marginTop: 30,
    marginBottom: 20,
    textAlign: "center"
  },
  input: {
    fontSize: 30,
    marginTop: 20,
    marginBottom: 20,
    textAlign: "center"

  },
  Click: {
    fontSize: 30,
    marginTop: 20,
    marginBottom: 30,
    textAlign: "center",
    borderWidth:1

  },

});
