  const [atividade, setAtividade] = React.useState()
  const [lista, setLista] = React.useState(["TOMAR BANHO", "CAFE"])

  const Click=() =>{
    setLista([...lista, atividade]);
    setAtividade(" "); 
  }
   const Remover=() =>{
     setAtividade("")

  }

  return (

    <SafeAreaView style={styles.container}>

      <View style={{backgroundColor:"#FFC582" }}>
        <Text style={styles.title1}>Tarefas Diarias</Text>
      </View>
      <View style={styles.Indroduzir}>
        <TextInput placeholder="Atividade"
          style={styles.input} value={atividade} onChangeText={setAtividade} />
      </View>
      <View style={styles.botao}>
      <Button title='Click' style={styles.botao}
      onPress={() =>Click()}/>
  </View>

     {lista.map((valor) =>(
      <View style={{flexDirection: "row", padding:10}}>
