import React from 'react';
import { StyleSheet, SafeAreaView, Text, Image, View, TouchableHighlight, StatusBar, TextInput, Button } from 'react-native';

export default function App() {
  const [atividade, setAtividade] = React.useState()
  const [lista, setLista] = React.useState(["TOMAR BANHO", "CAFE"])

  const adicionar=() =>{
    setLista([...lista, atividade]);
    setAtividade(" ");
  }

  return (

    <SafeAreaView style={styles.container}>

      <View style={{backgroundColor:"#00FD65" }}>
        <Text style={styles.title1}>Tarefas Diasrias</Text>
      </View>
      <View style={styles.Indroduzir}>
        <TextInput placeholder="Atividade"
          style={styles.input} value={atividade} onChangeText={setAtividade} />
      </View>
      <View styles={styles.Botao}>
        <TouchableHighlight onPress={() => adicionar()}>
          <View style={styles.Click} >
            <Text>Click</Text>
          </View>
        </TouchableHighlight>
        </View>
     {lista.map((valor) =>(
      <View style={{flexDirection: "row", padding:30}}>
      <Text style={{fontSize:20,flex:1}}>{valor}</Text>
      <TouchableHighlight onPress={() => adicionar()}>
          <View style={styles.Remover} >
            <Text>Remover</Text>
          </View>
        </TouchableHighlight>
      </View>
      ))}
    </SafeAreaView>

  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    padding:10,
  },
  title1: {
    fontSize: 30,
    marginTop: 30,
    marginBottom: 20,
    textAlign: "center"
  },
  input: {
    fontSize: 30,
    marginTop: 20,
    marginBottom: 20,
    borderWidth:1,
    backgroundColor:"#00E39B",
    textAlign: "center"  
  },
  Click: {
    fontSize: 40,
    marginTop: 20,
    marginBottom:10,
    padding: 20,
    backgroundColor:"#00E39B",
    textAlign: "center",
    borderWidth:1,
  },
  Botao:{
padding:10,
  },
  Indroduzir:{
padding:10,
  },
  Remover:{
backgroundColor:"#FC2B00"
  },

});
