import { TextInput, View } from "react-native";

export default function Campo(props) {
    return (
      <View style={{pading:20}}>
        <TextInput placeholder={props.input} style={styles.input}  />
      </View>
    )
  }

  const styles = StyleSheet.create({
    
    input:{
      alignItems: 'center',
      fontSize:30,
      marginTop:10,
     marginBottom:10,
    },

  });
