import { StyleSheet, TextInput, View } from "react-native";

export default function Campo(props) {
    return (
      <View style={styles.container}>
        <TextInput placeholder={props.input} style={styles.input}  />
      </View>
    )
  }

  const styles = StyleSheet.create({
    container: {
      flex: 1,
      backgroundColor: '#fff',
      alignItems: 'center',
     
    },

    input:{
      alignItems: 'center',
      fontSize:30,
      marginTop:10,
     marginBottom:10,
     borderWidth:1 
    },

  });
