import { StyleSheet,StatusBar,TouchableOpacity, Text, View,TextInput } from 'react-native';


export default function Registrar(props) {
  const [estilos, setEstilos]= React.useState()
    return (
      <View style={styles.container}>
        <TextInput placeholder={props.estilos(estilos)} style={styles.input} value={estilos} onChangeText={setEstilos} />

          {/* <Campo estilos="Nome"/>
          <Campo estilos="E-mail" />
          <Campo estilos="Senha" />
          <Campo estilos="Senha" /> */}
             <TouchableOpacity style={styles.button} onPress={() => props.onMostrar(estilos)}>
          <Text style={styles.textButton}>Registrar</Text>
        </TouchableOpacity>
      </View>
    )
  }

  const styles = StyleSheet.create({
    container: {
        backgroundColor: '#fff',
    },

    textButton:{
     padding:20,
 
      fontSize:20,
      marginTop:20,
     marginBottom:10,
     borderWidth:1 ,
     textAlign:"center",
     backgroundColor:"#78D629"

    },

  });
