import React, { useEffect, useState } from "react";
import AsyncStorage from "@react-native-async-storage/async-storage";

export const ListContext = React.createContext();

export const ListProvider = ({ children }) => {
  const [productList, setProductList] = useState([]);
  const [productListFilter, setProductListFilter] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState();
  const [categoryList, setCategoryList] = useState([
    { key: "Trabalho", value: "Trabalho", icon: "iconWork.png" },
    { key: "Casa", value: "Casa", icon: "iconCasa.png" },
    { key: "Pesoal", value: "Pesoal", icon: "iconPerfil.png" },
    { key: "Family", value: "Family", icon: "iconFamily.png" },
    { key: "Estudar", value: "Estudar", icon: "studie.png" },
  ]);

  const addTask = (oqComprar) => {
    setProductList([...productList, oqComprar]);
  };

  const removeTask = (oqComprar) => {
    console.log(oqComprar);
    productList.splice(productList.indexOf(oqComprar), 1)
    setProductList([...productList]);
  };

  const restoreList = async () => {
    try {
      const tasks = await AsyncStorage.getItem("tasks");
      setProductList(JSON.parse(tasks) || []);
    } catch (err) {
      setProductList([]);
    }
  };

  const saveList = async () => {
    try {
      await AsyncStorage.setItem("tasks", JSON.stringify(productList));
    } catch (err) {
      console.log("AQUI", err);
    }
  };


  useEffect(() => {
    restoreList();
  }, []);

  useEffect(() => {
    saveList();
  }, [productList]);

  useEffect(() => {
    setProductListFilter(
      productList.filter((oqComprar) => oqComprar.category == selectedCategory || selectedCategory == null)
    );
  }, [selectedCategory, productList]);

  return (
    <ListContext.Provider
      value={{
        productList,
        addTask,
        removeTask,
        categoryList,
        setSelectedCategory,
        selectedCategory,
        productListFilter,
      }}
    >
      {children}
    </ListContext.Provider>
  );
};
