import React, { useEffect, useState } from "react";
import AsyncStorage from "@react-native-async-storage/async-storage";

export const ListContext = React.createContext();

export const ListProvider = ({ children }) => {
  const [tasksList, setTasksList] = useState([]);
  const [tasksListFilter, setTasksListFilter] = useState([]);

  const categoryList = [
    { key: "work", value: "work" },
    { key: "home", value: "home" },
    { key: "personal", value: "personal" },
    { key: "family", value: "family" },
    { key: "Studies", value: "Studies" },
  ];

  const addTask = (tarefa) => {
    tasksList.push([tarefa.titulo, tarefa.category, tarefa.data]);
  };

  const funcaoFiltradora = (selected) => {
    setTasksListFilter(tasksList.filter((e) => tarefa.category == selected));
  };
  useEffect(() => {
    funcaoFiltradora();
  }, []);

  const selecionar = (selecionado) => {
    setSelected(selecionado);
  };

  return (
    <ListContext.Provider value={{ tasksList, addTask, categoryList, selecionar, tasksListFilter }}>
      {children}
    </ListContext.Provider>
  );
};
