import React, { useState, useContext } from "react";
import {
  StyleSheet,
  SafeAreaView,
  View,
  StatusBar,
  Text,
  TouchableOpacity,
  Modal,
  TextInput,
} from "react-native";
import Icon from "@expo/vector-icons/FontAwesome5";
import { ListContext } from "../../context/ListCreate";
import SelectList from "react-native-dropdown-select-list"

export function ModalTask({ handlerClose }) {

  const  {categoryList}  = React.useContext(ListContext)
  const  {addTask}  = React.useContext(ListContext)

  const [ahfaze, setAhfaze] = React.useState();
  const [category, setCategory] = React.useState();
  const [data, setData] = React.useState();
  const closeBtn = React.useRef(null);

  const tarefa = {
    titulo: ahfaze,
    category: category,
    data: data
  }

  const buttonAdd = () => {
    try{
      addTask(tarefa)
    }catch(err){
      console.log("task nao adicionada!")
    }
  }
  const onClose = (event) => {
    if (event.target == closeBtn.current) handlerClose();
  };
  return (
    <TouchableOpacity activeOpacity={false} style={styles.sair} onPress={onClose} ref={closeBtn}>
      <View style={styles.container}>
        <Text>Adicione uma Tarefa</Text>
        <View>
        <View style={styles.containerAdd}>
          <TextInput
            placeholder="Adicione uma Tarefa:"
            placeholderTextColor={"#000"}
            style={styles.inputs}
            onChangeText={setAhfaze} value={ahfaze}
            />
          <TouchableOpacity style={styles.buttonAdicionar} onPress={buttonAdd}>
            <Icon name="plus-circle" size={30} color={"000"} />
          </TouchableOpacity>
        </View>
        <View style={styles.containerDrop}>
        <SelectList
            data={categoryList}
            setSelected={setCategory}
            boxStyles={styles.dropButton}

          />
          <TextInput placeholder="Data" style={styles.inputData} onChangeText={setData} value={data}/>
        </View>
      </View>
      </View>
    </TouchableOpacity>
  );
}

const styles = StyleSheet.create({
  sair: {
    flex: 1,
    marginTop: 70,
    alignItems: 'center'
  },
  container: {
    width: "95%",
    height: "50%",
    backgroundColor: "#fff",
  },
  containerAdd: {
    flexDirection: "row",
    width: "90%",
    height: 40,
    borderRadius: 10,
    backgroundColor: "#CACACA",
    marginLeft: 20,
    marginTop: 20,
  },
  containerDrop:{
    flexDirection: 'row',
    justifyContent: 'space-around'
  },
  header: {
    width: "100%",
    height: 200,
    backgroundColor: "#000",
    borderWidth: 1,
    borderBottomRightRadius: 100,
  },
  inputs: {
    width: "80%",
    height: 40,
    borderRadius: 10,
    marginBottom: 20,
    padding: 10,
    backgroundColor: "#CACACA",
    color: "#000",
    fontSize: 18,
  },
  inputData: {
    width: "40%",
    height: 40,
    borderRadius: 10,
    marginBottom: 20,
    marginTop: 15,
    padding: 10,
    backgroundColor: "#CACACA",
    color: "#000",
    fontSize: 18,
  },
  buttonAdicionar: {
    width: "20%",
    height: 40,
    backgroundColor: "#CACACA",
    borderRadius: 10,
    justifyContent: "center",
    alignItems: "center",
  },
  buttonTxtA: {
    fontSize: 20,
    fontWeight: "bold",
    color: "#000",
  },
  dropButton:{
    height: 40,
    marginTop: 15,
    marginLeft: 5,
    borderRadius: 10,
  },
  menu:{
    marginTop: 1,
    borderRadius: 10,
    color: '#000'
  }
});
