import React, { useState } from "react";
import { StyleSheet, SafeAreaView, View, StatusBar, Text, TouchableOpacity, Modal, TextInput} from "react-native";
import Icon from '@expo/vector-icons/FontAwesome5';


export function ModalTask({ handlerClose }){

    return( 
      <View style={styles.container}>
              <Text>Adicione uma Tarefa</Text>
                <View style={styles.containerAdd}>
                  <TextInput placeholder='Adicione uma Tarefa:' 
                    placeholderTextColor={"#000"} style={styles.inputs} 
                    />
                  <TouchableOpacity style={styles.buttonAdicionar}>
                    <Icon name="plus-circle" size={30} color={"000"}/>
                  </TouchableOpacity>
                  </View>
                <TouchableOpacity style={styles.sair} onPress={handlerClose}>  
                </TouchableOpacity>
            </View>
    )
}

const styles = StyleSheet.create({
  sair:{
    flex: 1,
  },
    container: {
        width: '90%',
        height: '60%',
        backgroundColor: '#EBC12C',
      },
      containerAdd:{
        flexDirection: 'row',
        width: '90%',
        height: 40,
        borderRadius: 10,
        backgroundColor: '#CACACA',
        marginLeft: 20,
        marginTop: 20,
      },
      header:{
        width: '100%',
        height :200,
        backgroundColor: '#000',
        borderWidth: 1,
        borderBottomRightRadius: 100,
        
      },
      inputs:{
        width: '80%',
        height: 40,
        borderRadius: 10,
        marginBottom: 20,
        padding: 10,
        backgroundColor: '#CACACA',
        color: "#000",
        fontSize: 18
    },
      buttonAdicionar:{
        width: '20%',
        height: 40,
        backgroundColor: '#CACACA',
        borderRadius: 10,
        justifyContent: 'center',
        alignItems: 'center',
      },
      buttonTxtA:{
        fontSize: 20,
        fontWeight: 'bold',
        color: '#000'
      },
})
