import React from "react";
import { StyleSheet, SafeAreaView, View, StatusBar, Text, TouchableOpacity} from "react-native";
import { useNavigation } from "@react-navigation/native";
import Icon from '@expo/vector-icons/FontAwesome5';
import Ionicons from '@expo/vector-icons/Ionicons';

import Title from "./Title";

export default function TarefaItem(props){

    const navigation = useNavigation()

    return(
            <View style={styles.container}>
                <View style={styles.containerTask}>
                     <Title title={props.title} />
                </View>
                <View style={styles.containerButtons} >
                    <TouchableOpacity style={styles.button}>
                        <Icon name="trash-alt" size={30} color={"#000"}/>
                    </TouchableOpacity>
                    <TouchableOpacity style={styles.button}>
                        <Ionicons name="checkmark-done-circle" size={30} color={"#000"}/>
                    </TouchableOpacity>
                </View>
            </View>
    )
}

const styles = StyleSheet.create({
 container:{
    flexDirection: "row", 
    width: "90%",
    marginBottom: 10, 
    borderBottomWidth: 1, 
    borderColor: "#eee", 
    paddingBottom: 10
},
containerTask:{
    width: "80%",
    height: 50,
    borderTopWidth: 1,
    borderBottomWidth: 1,
    backgroundColor: '#fff'
},
containerButtons:{
    
},
button:{
    width: "10%",
    height: 50,
},
  txtLista:{
    fontSize: 20,
    fontWeight: '700',
  },
})
