import React, { useEffect, useState } from "react";
import AsyncStorage from "@react-native-async-storage/async-storage";

export const ListContext = React.createContext();

export const ListProvider = ({ children }) => {
  const [tasksList, setTasksList] = useState([]);
  const [tasksListFilter, setTasksListFilter] = useState([]);
  const [tasksListDone, setTasksListDone] = React.useState([])
  const [tasksDone, setTasksDone] = React.useState(false)
  const [tasksDelete, setTasksDelete] = React.useState()
  const [selectedCategory, setSelectedCategory] = useState();
  const [categoryList, setCategoryList] = useState([
    { key: "Trabalho", value: "Trabalho" },
    { key: "Casa", value: "Casa" },
    { key: "Pesoal", value: "Pesoal" },
    { key: "Family", value: "Family" },
    { key: "Estudar", value: "Estudar" },
  ]);

  const addTask = (tarefa) => {
    tasksList.push(tarefa);
  };
  
  useEffect(() => {
    if(tasksList.filter((tarefa) => tarefa.tasksDone == false)){
    setTasksListFilter(
      tasksList.filter((tarefa) => tarefa.category == selectedCategory)
      )}else{ setTasksListFilter(
        tasksListDone.filter((tarefa) => tarefa.category == selectedCategory)
      )}
  }, [selectedCategory, tasksList, tasksDone]);

  return (
    <ListContext.Provider
      value={{
        tasksList,
        addTask,
        categoryList,
        setSelectedCategory,
        tasksListFilter,
        tasksListDone,
        setTasksDone,
        tasksDone,
        setTasksDelete,
        tarefaFeita
      }}
    >
      {children}
    </ListContext.Provider>
  );
};
