import React, {useEffect, useState} from "react";
import AsyncStorage from '@react-native-async-storage/async-storage';

export const ListContext = React.createContext()

export const ListProvider = ({children}) => {
    
    
    const [tasksList, setTasksList] = useState([]) 

    const categoryList = [ {key: 'work', value: 'work'}, {key: 'home', value: 'home'}, {key: 'personal', value: 'personal'}, {key: 'family', value: 'family'}, {key: 'Studies', value: 'Studies'} ]
    
    const addTask = (tarefa) => {
        tasksList.push()
    }
    
    return (
        <ListContext.Provider value={{tasksList, setTasksList, addTask, categoryList}}>
            { children }
        </ListContext.Provider>
    )
}
