        // </ScrollView>
        <View>
            <TarefaItem title="Planejamento"/>
            <TarefaItem title="Organização do app"/>
            <TarefaItem title="Divisao do app"/>
            <TarefaItem title="Implementaçao do app"/>
            <TarefaItem title="Fazer a Logica"/>
            <TarefaItem title="Estilizar"/>
            <TarefaItem title="Acabamentos Finais"/>
        </View>
    )
}

const styles = StyleSheet.create({
container:{
    padding: 10, 
    flex: 1,
}
})
