import React, { useState, useContext } from "react";
import {
  StyleSheet,
  SafeAreaView,
  View,
  StatusBar,
  Text,
  TouchableOpacity,
  Modal,
  TextInput,
} from "react-native";
import Icon from "@expo/vector-icons/FontAwesome5";
import { ListContext } from "../../context/ListCreate";
import SelectDropdown from 'react-native-select-dropdown';

export function ModalTask({ handlerClose }) {

  const  categoryList  = React.useContext(ListContext)

  const [tarefa, setTarefa] = React.useState({});
  const closeBtn = React.useRef(null);

  const onClose = (event) => {
    if (event.target == closeBtn.current) handlerClose();
  };
  return (
    <TouchableOpacity activeOpacity={false} style={styles.sair} onPress={onClose} ref={closeBtn}>
      <View style={styles.container}>
        <Text>Adicione uma Tarefa</Text>
        <View>
        <View style={styles.containerAdd}>
          <TextInput
            placeholder="Adicione uma Tarefa:"
            placeholderTextColor={"#000"}
            style={styles.inputs}
            />
          <TouchableOpacity style={styles.buttonAdicionar}>
            <Icon name="plus-circle" size={30} color={"000"} />
          </TouchableOpacity>
        </View>
        <View>
        <SelectDropdown dropdownStyle={styles.menu} buttonStyle={styles.dropButton}
            data={categoryList}
            onSelect={(selectedItem, index) => {
              console.log(selectedItem, index);
            }}
            buttonTextAfterSelection={(selectedItem, index) => {
              return selectedItem;
            }}
            rowTextForSelection={(item, index) => {
              return item;
            }}
          />
          <TextInput placeholder="Data" style={styles.inputs}/>
        </View>
      </View>
      </View>
    </TouchableOpacity>
  );
}

const styles = StyleSheet.create({
  sair: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center'
  },
  container: {
    width: "95%",
    height: "70%",
    backgroundColor: "#000",
  },
  containerAdd: {
    flexDirection: "row",
    width: "90%",
    height: 40,
    borderRadius: 10,
    backgroundColor: "#CACACA",
    marginLeft: 20,
    marginTop: 20,
  },
  header: {
    width: "100%",
    height: 200,
    backgroundColor: "#000",
    borderWidth: 1,
    borderBottomRightRadius: 100,
  },
  inputs: {
    width: "80%",
    height: 40,
    borderRadius: 10,
    marginBottom: 20,
    padding: 10,
    backgroundColor: "#CACACA",
    color: "#000",
    fontSize: 18,
  },
  buttonAdicionar: {
    width: "20%",
    height: 40,
    backgroundColor: "#CACACA",
    borderRadius: 10,
    justifyContent: "center",
    alignItems: "center",
  },
  buttonTxtA: {
    fontSize: 20,
    fontWeight: "bold",
    color: "#000",
  },
  dropButton:{
    marginTop: 15,
    marginLeft: 20,
    borderRadius: 10,
  },
  menu:{
    marginTop: 1,
    borderRadius: 10,
    color: '#000'
  }
});
