import React, {useContext} from "react";
import { StyleSheet, SafeAreaView, View, StatusBar, Text, TouchableOpacity, ScrollView} from "react-native";
import { useNavigation } from "@react-navigation/native";

import TarefaItem from "./TarefaItem";
import { ListContext } from "../../context/ListCreate";

export default function ListaTarefas(props){

    const  {  tasksListFilter, tasksListDone }  = React.useContext(ListContext)

    return(
        <View>
        <ScrollView style={styles.container}>
             { tasksListFilter.map((t) => <TarefaItem title={t.titulo} data={t.data} />)}
        </ScrollView>
        <View style={{ backgroundColor: '#fff',}}>
            <Text style={styles.title}>Tarefas Feitas!!!</Text>
        </View>
        <ScrollView style={styles.container}>
             { tasksListDone.map((t) => <TarefaItem title={t.titulo} data={t.data} />)}
        </ScrollView>
        </View>
    )
}

const styles = StyleSheet.create({
container:{
    padding: 10, 
    flex: 1,
},
title:{
    fontSize: 40,
    fontWeight: 'bold',
    color: '#000',
    marginLeft: 40,
  },
})
