import React, {useContext, useState} from 'react';
import { StyleSheet, StatusBar, SafeAreaView, Text, View, TextInput, ScrollView, TouchableOpacity, Modal } from 'react-native';
import Icon from '@expo/vector-icons/FontAwesome5';

import Buttons from '../components/Button';
import Inputs from '../components/Inputs'
import ListaTarefas from '../components/ArrayTarefa';
import { ListContext } from '../../context/ListCreate';
import { ModalTask } from '../components/Modal';

export default function ListasTarefas() {

  const [ visible, setVisible ] = React.useState(false)
  const [task, setTask ] = React.useState()
  const  { addTask } = React.useContext(ListContext)

  const buttonAdd = () => {
    try{
      addTask(task, setTask)
    }catch(err){
      console.log("task nao adicionada!")
    }
  }

  return (
    <SafeAreaView style={styles.container}>
      <StatusBar style="auto" />
      <View style={styles.header}>
        <Text style={styles.title}>Lista de Tarefas!!!</Text>
        <View style={styles.containerAdd}>
          <TextInput placeholder='Adicione uma Tarefa:' 
          placeholderTextColor={"#000"} style={styles.inputs} 
          onChangeText={setTask} value={task} />
          <TouchableOpacity style={styles.buttonAdicionar} onPress={buttonAdd}>
            <Icon name="plus-circle" size={30} color="000"/>
          </TouchableOpacity>
        </View>
      </View>
      <ScrollView>
      <View style={styles.lista}>
        <ListaTarefas/>
      </View>
      </ScrollView>
        <TouchableOpacity style={styles.buttonAdicionar} onPress={() => setVisible(true)}>
            <Icon name="plus-circle" size={80} color="000"/>
          </TouchableOpacity>
          <Modal animationType="slide"
                    transparent={true}
                    visible={visible}
                    style={styles.modal}>
                      <ModalTask handlerClose={setVisible(false)}/>
                    </Modal>
        <View style={styles.tab}>
          <Buttons style={styles.button} styleTxt={styles.buttonTxt} buttonName='T'/>
          <Buttons style={styles.button} styleTxt={styles.buttonTxt} buttonName='C'/>
          <Buttons style={styles.button} styleTxt={styles.buttonTxt} buttonName='P'/>
          <Buttons style={styles.button} styleTxt={styles.buttonTxt} buttonName='F'/>
          <Buttons style={styles.button} styleTxt={styles.buttonTxt} buttonName='E'/>
        </View>
    </SafeAreaView>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#EBC12C',
  },
  containerAdd:{
    flexDirection: 'row',
    width: '90%',
    height: 40,
    borderRadius: 10,
    backgroundColor: '#CACACA',
    marginLeft: 20,
    marginTop: 20,
  },
  tab:{
    flexDirection: 'row',
    justifyContent: 'space-around',
    width: '100%',
    height: 80,
    backgroundColor: '#000',
    borderWidth: 1,
    borderTopLeftRadius: 100,
  },
  lista:{
    flex: 1,
  },
  listaItem:{
    height: 50,
    borderTopWidth: 1,
    borderBottomWidth: 1,
    backgroundColor: '#fff'
  },
  header:{
    width: '100%',
    height :200,
    backgroundColor: '#000',
    borderWidth: 1,
    borderBottomRightRadius: 100,
    
  },
  inputs:{
    width: '80%',
    height: 40,
    borderRadius: 10,
    marginBottom: 20,
    padding: 10,
    backgroundColor: '#CACACA',
    color: "#000",
    fontSize: 18
},
  buttonAdicionar:{
    width: 80,
    height: 80,
    backgroundColor: '#fff',
    borderRadius: 200/2,
    justifyContent: 'center',
    alignItems: 'center',
  },
  buttonTxtA:{
    fontWeight: 'bold',
    color: '#000'
  },
  button:{
    width: 60,
    marginTop: 15,
    justifyContent: 'center',
    alignItems: 'center',
  },
  buttonTxt:{
    fontSize: 40,
    fontWeight: 'bold',
    color: '#fff',
  },
  title:{
    fontSize: 40,
    fontWeight: 'bold',
    color: '#fff',
    marginLeft: 20,
    marginTop: 10,
  },
  txtLista:{
    fontSize: 20,
    fontWeight: '700',
  },
});
